// ---------------Zähler
var impulse = flow.get("impulse") || 0
var counterdefault = flow.get("counterdefault") || 0


// ---------------Tankfüllung
var startdefault = flow.get("startdefault") || 0
var tankfuellung = flow.get("tankfuellung") || 0

// ---------------Zähler
if (msg.topic == "zaehler"){
    impulse = 0
    flow.set("counterdefault", msg.payload.set)

} else {
    impulse ++
}

// ---------------Tankfüllung
if (msg.topic == "fuellung"){
    tankfuellung = flow.set("tankfuellung", msg.payload.liter + tankfuellung)
    tankfuellung = flow.get("tankfuellung")
    } 

if (msg.topic == "initialisierungTank"){
    flow.set("startdefault", msg.payload.liter)
    flow.set("tankfuellung", 0)
    startdefault = flow.get("startdefault")
}

var counter = counterdefault + (impulse/10)

flow.set("impulse", impulse)

// ---------------Message Ausgabe
msg.impulse = impulse/10
msg.zaehler = counter
msg.tank = tankfuellung + startdefault - impulse/10

return msg;

